package milestone1;

public class CF5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'a';
		if (Character.isLowerCase(ch)) 
			System.out.println(ch + "->" + Character.toUpperCase(ch));
		else
			System.out.println(ch + "->" + Character.toLowerCase(ch));

	}

}
